class MetaMemeEventHandler {
  private readonly eventEmitter: EventEmitter;
  private readonly subscribers: Map<string, Subscriber>;

  async handleSemanticEvent(
    event: SemanticEvent
  ): Promise<EventHandlingResult> {
    const eventContext = await this.buildEventContext(event);
    
    return this.processEventWithRetry(event, eventContext, {
      maxRetries: 3,
      backoffMs: 1000
    });
  }

  private async processEventWithRetry(
    event: SemanticEvent,
    context: EventContext,
    options: RetryOptions
  ): Promise<EventHandlingResult> {
    let attempt = 0;
    
    while (attempt < options.maxRetries) {
      try {
        const result = await this.processEvent(event, context);
        await this.notifySubscribers(result);
        
        return result;
      } catch (error) {
        attempt++;
        if (attempt === options.maxRetries) {
          throw new EventProcessingError(error);
        }
        await this.delay(options.backoffMs * attempt);
      }
    }
  }

  private async notifySubscribers(
    result: EventHandlingResult
  ): Promise<void> {
    const notifications = Array.from(this.subscribers.values())
      .map(subscriber => this.notifySubscriber(subscriber, result));
    
    await Promise.allSettled(notifications);
  }
}
