    return {
      matches: this.rankMatches(matches),
      confidence: this.calculateMatchConfidence(matches),
      metadata: this.generateMatchMetadata(matches)
    };
  }

  private async findPatternMatches(
    encoded: EncodedInput
  ): Promise<PatternMatch[]> {
    const prediction = await this.neuralNetwork.predict(encoded);
    return this.decodePrediction(prediction);
  }
