add :: Nat -> Nat -> Nat
add n m =
  case n of {
   O -> m;
   S p -> S (add p m)}

toNat :: Positive -> Nat
toNat x =
  iterOp add x (S O)

iterOp :: (a1 -> a1 -> a1) -> Positive -> a1 -> a1
iterOp op p a =
  case p of {
   XI p0 -> op a (iterOp op p0 (op a a));
   XO p0 -> iterOp op p0 (op a a);
   XH -> a}

fold_right :: (a2 -> a1 -> a1) -> a1 -> (List a2) -> a1
fold_right f a0 l =
