data Comparison =
   Eq
 | Lt
 | Gt
  deriving ( Generic, Show,Lift, --GQLType,
             Data, Typeable)

compare10 :: T3 -> T3 -> Comparison
compare10 l1 l2 =
  case l1 of {
   Lzero -> case l2 of {
             Lzero -> Eq;
             _ -> Lt};
   Level s1 ->
    case l2 of {
     Lzero -> Gt;
     Level s2 -> compare5 s1 s2;
     Lvar _ -> Lt};
   Lvar n -> case l2 of {
              Lvar m -> my_compare n m;
              _ -> Gt}}

compare2 :: Z -> Z -> Comparison
compare2 x y =
  case x of {
   Z0 -> case y of {
          Z0 -> Eq;
          Zpos _ -> Lt;
          Zneg _ -> Gt};
   Zpos x' -> case y of {
               Zpos y' -> compare0 x' y';
               _ -> Gt};
   Zneg x' -> case y of {
               Zneg y' -> compOpp (compare0 x' y');
               _ -> Lt}}
compOpp :: Comparison -> Comparison
compOpp r =
  case r of {
   Eq -> Eq;
   Lt -> Gt;
   Gt -> Lt}
this :: T_0 -> T4
this t =
  t

compare0 :: Positive -> Positive -> Comparison
compare0 =
  compare_cont Eq

compare_cont :: Comparison -> Positive -> Positive -> Comparison
compare_cont r x y =
  case x of {
   XI p ->
    case y of {
     XI q -> compare_cont r p q;
     XO q -> compare_cont Gt p q;
     XH -> Gt};
   XO p ->
    case y of {
     XI q -> compare_cont Lt p q;
     XO q -> compare_cont r p q;
     XH -> Gt};
   XH -> case y of {
          XH -> r;
          _ -> Lt}}

compare27 :: T24 -> T24 -> Comparison
compare27 x y =
