bal :: T4 -> T_ -> T4 -> Tree
bal l x r =
  let {hl = height l} in
  let {hr = height r} in
  case ltb0 (add2 hr _2) hl of {
   True ->
    case l of {
     Leaf -> assert_false l x r;
     Node _ ll lx lr ->
      case leb1 (height lr) (height ll) of {
       True -> create ll lx (create lr x r);
       False ->
        case lr of {
         Leaf -> assert_false l x r;
         Node _ lrl lrx lrr -> create (create ll lx lrl) lrx (create lrr x r)}}};
   False ->
    case ltb0 (add2 hl _2) hr of {
     True ->
      case r of {
       Leaf -> assert_false l x r;
       Node _ rl rx rr ->
        case leb1 (height rl) (height rr) of {
         True -> create (create l x rl) rx rr;
         False ->
          case rl of {
           Leaf -> assert_false l x r;
           Node _ rll rlx rlr ->
            create (create l x rll) rlx (create rlr rx rr)}}};
     False -> create l x r}}

-- fixme renamed from compare
my_compare :: Nat -> Nat -> Comparison
my_compare n m =
  case n of {
   O -> case m of {
         O -> Eq;
         S _ -> Lt};
   S n' -> case m of {
            O -> Gt;
            S m' -> my_compare n' m'}}

add3 :: T_ -> Tree -> Tree
add3 x s =
  case s of {
   Leaf -> Node _1 Leaf x Leaf;
   Node h l y r ->
    case case x of {
          Lzero -> case y of {
                    Lzero -> Eq;
                    _ -> Lt};
          Level s1 ->
           case y of {
            Lzero -> Gt;
            Level s2 -> compare5 s1 s2;
            Lvar _ -> Lt};
          Lvar n -> case y of {
                     Lvar m -> my_compare n m;
                     _ -> Gt}} of {
     Eq -> Node h l y r;
     Lt -> bal (add3 x l) y r;
     Gt -> bal l y (add3 x r)}}

